          this.updateGraphicData();
        }
      );
  }

  public getParameters(): void {
    this.httpClient
      .get('https://proyecto-pif-arq.azurewebsites.net/api/getParameters')
      .subscribe(
        (response: any) => {
          console.log(response);
          this.parametros = response;
        },
        (error) => {}
      );
  }
  public getLimites(): void {
    this.httpClient
      .get('https://proyecto-pif-arq.azurewebsites.net/api/getLimites')
      .subscribe(
        (response: any) => {
          console.log(response);
          this.limites = response;
        },
        (error) => {}
      );
  }

  public guardarCorreccion() {
    const body: any = {
      nuevoValorTemp: this.formParametros?.get('tmpCorreccion')?.value,
      nuevoValorHumedad: this.formParametros?.get('hmdCorreccion')?.value,
    };
    this.httpClient
      .post('https://proyecto-pif-arq.azurewebsites.net/api/calibrar', body)
      .subscribe(
        (response: any) => {
          console.log(response);
          if(response?.resultado){
            this.getParameters();
            this.formParametros.reset();
          }
        },
        (error) => {}
      );
  }

  public guardarLimites() {
    const body: any = {
      tempLimitMin: this.formParametros?.get('minTemp')?.value,
      tempLimitMax: this.formParametros?.get('maxTemp')?.value,
      hmdLimitMin: this.formParametros?.get('minHum')?.value,
      hmdLimitMax: this.formParametros?.get('maxHum')?.value,
    };
    this.httpClient
      .post('https://proyecto-pif-arq.azurewebsites.net/api/updateLimites', body)
      .subscribe(
        (response: any) => {
          console.log(response);
          if(response?.resultado){
            this.getParameters();
            this.formParametros.reset();
