import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import data from './data.json';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent {
  title = 'temperature-and-humedity-control';
  first = 0;
  rows = 10;

  public basicData: any;
  public basicOptions: any;
  public tableData: any[] = data;

  constructor(private httpClient: HttpClient) {
    this.basicOptions = {
      plugins: {
        legend: {
          labels: {
            color: '#495057',
          },
        },
      },
      scales: {
        x: {
          ticks: {
            color: '#495057',
          },
          grid: {
            color: '#ebedef',
          },
        },
        y: {
          ticks: {
            color: '#495057',
          },
          grid: {
            color: '#ebedef',
          },
        },
      },
    };
    this.refreshData();
  }

  public updateGraphicData(): void {
    let labels = [
      this.tableData[0].fecha.toString().split('T')[1],
      this.tableData[20].fecha.toString().split('T')[1],
      this.tableData[40].fecha.toString().split('T')[1],
      this.tableData[60].fecha.toString().split('T')[1],
      this.tableData[80].fecha.toString().split('T')[1],
      this.tableData[100].fecha.toString().split('T')[1],
      this.tableData[120].fecha.toString().split('T')[1],
      this.tableData[140].fecha.toString().split('T')[1],
      this.tableData[160].fecha.toString().split('T')[1],
      this.tableData[180].fecha.toString().split('T')[1],
      this.tableData[200].fecha.toString().split('T')[1],
    ];
    let dataTemperatura = [
      this.tableData[0].temperaturA_REAL,
      this.tableData[20].temperaturA_REAL,
      this.tableData[40].temperaturA_REAL,
      this.tableData[60].temperaturA_REAL,
      this.tableData[80].temperaturA_REAL,
      this.tableData[100].temperaturA_REAL,
      this.tableData[120].temperaturA_REAL,
      this.tableData[140].temperaturA_REAL,
      this.tableData[160].temperaturA_REAL,
      this.tableData[180].temperaturA_REAL,
      this.tableData[200].temperaturA_REAL,
    ];

    let dataHumedad = [
      this.tableData[0].humedaD_REAL,
      this.tableData[20].humedaD_REAL,
      this.tableData[40].humedaD_REAL,
      this.tableData[60].humedaD_REAL,
      this.tableData[80].humedaD_REAL,
      this.tableData[100].humedaD_REAL,
      this.tableData[120].humedaD_REAL,
      this.tableData[140].humedaD_REAL,
      this.tableData[160].humedaD_REAL,
      this.tableData[180].humedaD_REAL,
      this.tableData[200].humedaD_REAL,
    ];
    this.basicData = {
      labels,
      datasets: [
        {
          label: 'Temperatura',
          data: dataTemperatura,
          fill: false,
          borderColor: '#42A5F5',
          tension: 0.4,
        },
        {
          label: 'Humedad',
          data: dataHumedad,
          fill: false,
          borderColor: '#FFA726',
          tension: 0.4,
        },
      ],
    };
  }

  public refreshData(): void {
    this.httpClient
      .get('https://proyecto-pif-arq.azurewebsites.net/api/getSenses')
      .subscribe(
        (response: any) => {
          console.log(response);
          this.tableData = response;
          this.tableData=this.tableData.sort()
          this.updateGraphicData();
        },
        (error) => {
          this.tableData = data;
          this.updateGraphicData();
        }
      );
  }
}
<div class="container">
  <p-tabView>
    <p-tabPanel header="Datos">
      <div class="card">
        <div class="table-header">
          <h3>Registro de control de temperatura y humedad
          </h3>
          <p-button type="button" icon="pi pi-refresh" styleClass="p-button-text" (click)="refreshData();"></p-button>
        </div>
        <p-table [value]="tableData" [paginator]="true" [rows]="10" [showCurrentPageReport]="true"
          [tableStyle]="{'min-width': '20rem'}"
          currentPageReportTemplate="Mostrando {first} a {last} de {totalRecords} filas" [rowsPerPageOptions]="[5,10,25,50]">
          <ng-template pTemplate=" header">
            <tr>
              <th pSortableColumn="Dispositivo" style="width:20%">Dispositivo <p-sortIcon field="Dispositivo"></p-sortIcon>
              </th>
              <th pSortableColumn="temperaturA_REAL" style="width:20%">Temperatura <p-sortIcon field="temperaturA_REAL"></p-sortIcon>
              </th>
              <th pSortableColumn="humedaD_REAL" style="width:20%">Humedad <p-sortIcon field="humedaD_REAL"></p-sortIcon>
              </th>
              <th pSortableColumn="fecha" style="width:20%">Fecha <p-sortIcon field="fecha"></p-sortIcon>
              </th>
              <th pSortableColumn="fecha" style="width:20%">Hora <p-sortIcon field="fecha"></p-sortIcon>
              </th>
            </tr>
          </ng-template>
          <ng-template pTemplate="body" let-data>
            <tr>
              <td>{{data.dispositivo}}</td>
              <td>{{data.temperaturA_REAL}}</td>
              <td>{{data.humedaD_REAL}}</td>
              <td>{{data.fecha.toString().split('T')[0]}}</td>
              <td>{{data.fecha.toString().split('T')[1]}}</td>
            </tr>
          </ng-template>
        </p-table>
      </div>
    </p-tabPanel>
    <p-tabPanel header="Grafica">
      <div style="width: 100%;">
        <h4>Grafica de valores con intervalos entre 2-5m</h4>
        <p-chart type="line" [data]="basicData" [options]="basicOptions"></p-chart>
      </div>
    </p-tabPanel>
    <p-tabPanel header="Parametros">
        <p>
            At vero eos et accusamus et iusto odio dignissimos ducimus qui blanditiis praesentium voluptatum deleniti atque corrupti quos dolores et quas molestias excepturi sint occaecati cupiditate non provident, similique sunt in culpa
            qui officia deserunt mollitia animi, id est laborum et dolorum fuga. Et harum quidem rerum facilis est et expedita distinctio. Nam libero tempore, cum soluta nobis est eligendi optio cumque nihil impedit quo minus.
        </p>
    </p-tabPanel>
</p-tabView>






</div>
